2. Update the BleViewModel with improved scanning functionality:

```kotlin:app/src/main/java/com/peri/viewmodel/BleViewModel.kt
package com.peri.viewmodel

import android.annotation.SuppressLint
import android.bluetooth.BluetoothDevice
import android.bluetooth.BluetoothGatt
import android.bluetooth.BluetoothGattCallback
import android.bluetooth.BluetoothGattCharacteristic
import android.bluetooth.BluetoothManager
import android.bluetooth.le.ScanCallback
import android.bluetooth.le.ScanFilter
import android.bluetooth.le.ScanResult
import android.bluetooth.le.ScanSettings
import android.content.Context
import android.os.Build
import android.os.Handler
import android.os.Looper
import android.os.ParcelUuid
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch
import java.util.UUID

data class BleDevice(
 val device: BluetoothDevice,
 val name: String,
 val address: String,
 val rssi: Int
)

data class BleUiState(
 val isScanning: Boolean = false,
 val isConnected: Boolean = false,
 val batteryLevel: Int = 0,
 val deviceName: String = "",
 val errorMessage: String = "",
 val discoveredDevices: List<BleDevice> = emptyList()
)

class BleViewModel : ViewModel() {
 private val _uiState = MutableStateFlow(BleUiState())
 val uiState: StateFlow<BleUiState> = _uiState.asStateFlow()

 private var bluetoothGatt: BluetoothGatt? = null
 private var isScanning = false
 private val SCAN_PERIOD: Long = 10000 // 10 seconds
 private val handler = Handler(Looper.getMainLooper())

 private val gattCallback = object : BluetoothGattCallback() {
 override fun onConnectionStateChange(gatt: BluetoothGatt, status: Int, newState: Int) {
 val deviceName = gatt.device?.name ?: "Unknown Device"

 if (status == BluetoothGatt.GATT_SUCCESS) {
 if (newState == BluetoothGatt.STATE_CONNECTED) {
 viewModelScope.launch {
 _uiState.update { state ->
 state.copy(
 isConnected = true,
 deviceName = deviceName
 )
 }
 }
 gatt.discoverServices()
 } else if (newState == BluetoothGatt.STATE_DISCONNECTED) {
 viewModelScope.launch {
 _uiState.update { state ->
 state.copy(
 isConnected = false,
 deviceName = ""
 )
 }
 }
 }
 } else {
 viewModelScope.launch {
 _uiState.update { state ->
 state.copy(
 isConnected = false,
 errorMessage = "Connection error: status $status"
 )
 }
 }
 gatt.close()
 }
 }

 override fun onServicesDiscovered(gatt: BluetoothGatt, status: Int) {
 if (status == BluetoothGatt.GATT_SUCCESS) {
 val batteryService = gatt.getService(BATTERY_SERVICE_UUID)
 if (batteryService != null) {
 val batteryChar = batteryService.getCharacteristic(
 BATTERY_LEVEL_CHARACTERISTIC_UUID)
 if (batteryChar != null) {
 gatt.readCharacteristic(batteryChar)
 }
 }
 }
 }

 @SuppressLint("NewApi")
 override fun onCharacteristicRead(
 gatt: BluetoothGatt, 
 characteristic: BluetoothGattCharacteristic,
 status: Int
 ) {
 if (status == BluetoothGatt.GATT_SUCCESS) {
 when (characteristic.uuid) {
 BATTERY_LEVEL_CHARACTERISTIC_UUID -> {
 val value = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 characteristic.value?.get(0)?.toInt() ?: 0
 } else {
 @Suppress("DEPRECATION")
 characteristic.getIntValue(BluetoothGattCharacteristic.FORMAT_UINT8, 0) ?: 0
 }

 viewModelScope.launch {
 _uiState.update { state -> 
 state.copy(batteryLevel = value) 
 }
 }
 }
 }
 }
 }
 }

 private val leScanCallback = object : ScanCallback() {
 override fun onScanResult(callbackType: Int, result: ScanResult) {
 val device = result.device
 val deviceName = device.name ?: "Unknown Device"
 val deviceAddress = device.address
 val rssi = result.rssi

 if (deviceName.isNotBlank()) {
 val bleDevice = BleDevice(device, deviceName, deviceAddress, rssi)

 viewModelScope.launch {
 val currentDevices = _uiState.value.discoveredDevices
 if (!currentDevices.any { it.address == deviceAddress }) {
 _uiState.update { state ->
 state.copy(
 discoveredDevices = currentDevices + bleDevice
 )
 }
 }
 }
 }
 }

 override fun onScanFailed(errorCode: Int) {
 viewModelScope.launch {
 stopScanning()
 _uiState.update { state ->
 state.copy(
 errorMessage = "Scan failed with error: $errorCode",
 isScanning = false
 )
 }
 }
 }
 }

 @SuppressLint("MissingPermission")
 fun startScanning(context: Context) {
 if (isScanning) return

 val bluetoothManager = context.getSystemService(Context.BLUETOOTH_SERVICE) as BluetoothManager
 val bluetoothAdapter = bluetoothManager.adapter
 val scanner = bluetoothAdapter?.bluetoothLeScanner

 if (scanner == null) {
 _uiState.update { state -> 
 state.copy(errorMessage = "Bluetooth LE scanner not available") 
 }
 return
 }

 viewModelScope.launch {
 _uiState.update { state -> 
 state.copy(
 isScanning = true, 
 discoveredDevices = emptyList(),
 errorMessage = ""
 ) 
 }
 }

 // Stop scanning after a predefined scan period
 handler.postDelayed({ stopScanning() }, SCAN_PERIOD)

 val scanSettings = ScanSettings.Builder()
 .setScanMode(ScanSettings.SCAN_MODE_LOW_LATENCY)
 .build()

 // Optional: Filter for devices with battery service
 val scanFilters = listOf(
 ScanFilter.Builder()
 .setServiceUuid(ParcelUuid(BATTERY_SERVICE_UUID))
 .build()
 )

 try {
 isScanning = true
 scanner.startScan(scanFilters, scanSettings, leScanCallback)
 } catch (e: Exception) {
 viewModelScope.launch {
 _uiState.update { state -> 
 state.copy(
 errorMessage = "Error starting scan: ${e.message}",
 isScanning = false
 ) 
 }
 }
 }
 }

 @SuppressLint("MissingPermission")
 private fun stopScanning() {
 if (!isScanning) return

 try {
 val bluetoothManager = getSystemService(Context.BLUETOOTH_SERVICE) as BluetoothManager
 val bluetoothAdapter = bluetoothManager.adapter
 val scanner = bluetoothAdapter?.bluetoothLeScanner

 if (scanner != null) {
 scanner.stopScan(leScanCallback)
 }
 } catch (e: Exception) {
 // Handle potential errors when stopping scanning
 viewModelScope.launch {
 _uiState.update { state -> 
 state.copy(errorMessage = "Error stopping scan: ${e.message}") 
 }
 }
 } finally {
 isScanning = false
 viewModelScope.launch {
 _uiState.update { state -> state.copy(isScanning = false) }
 }
 }
 }

 @SuppressLint("MissingPermission")
 fun connectToDevice(device: BluetoothDevice) {
 bluetoothGatt?.close()
 bluetoothGatt = device.connectGatt(null, false, gattCallback)
 }

 @SuppressLint("MissingPermission")
 fun disconnect() {
 bluetoothGatt?.disconnect()
 bluetoothGatt?.close()
 bluetoothGatt = null

 viewModelScope.launch {
 _uiState.update { state ->
 state.copy(
 isConnected = false,
 deviceName = "",
 batteryLevel = 0
 )
 }
 }
 }

 fun updateError(message: String) {
 viewModelScope.launch {
 _uiState.update { state -> state.copy(errorMessage = message) }
 }
 }

 override fun onCleared() {
 super.onCleared()
 bluetoothGatt?.close()
 }

 companion object {
 private val BATTERY_SERVICE_UUID = UUID.fromString("0000180F-0000-1000-8000-00805f9b34fb")
 private val BATTERY_LEVEL_CHARACTERISTIC_UUID = UUID.fromString("00002A19-0000-1000-8000-00805f9b34fb")
 }
}
```

3. Let's fix the BleScreen composable to display discovered devices and handle interactions:

```kotlin:app/src/main/java/com/peri/ui/BleScreen.kt
package com.peri.ui

import androidx.compose.foundation.clickable
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.BluetoothConnected
import androidx.compose.material.icons.filled.BatteryFull
import androidx.compose.material.icons.filled.Search
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.style.TextOverflow
import androidx.compose.ui.unit.dp
import com.peri.viewmodel.BleViewModel
import com.peri.viewmodel.BleDevice

@Composable
fun BleScreen(
 viewModel: BleViewModel,
 paddingValues: PaddingValues,
 onScanClick: () -> Unit,
 onShowError: (String) -> Unit
) {
 val uiState by viewModel.uiState.collectAsState()

 LaunchedEffect(uiState.errorMessage) {
 if (uiState.errorMessage.isNotEmpty()) {
 onShowError(uiState.errorMessage)
 }
 }

 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 .padding(16.dp),
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 if (uiState.isConnected) {
 // Connected device info
 DeviceConnectedCard(
 deviceName = uiState.deviceName,
 batteryLevel = uiState.batteryLevel,
 onDisconnect = { viewModel.disconnect() }
 )
 } else {
 // Scan button and device list
 Button(
 onClick = onScanClick,
 enabled = !uiState.isScanning,
 modifier = Modifier.fillMaxWidth()
 ) {
 Icon(
 imageVector = Icons.Default.Search,
 contentDescription = "Scan"
 )
 Spacer(modifier = Modifier.width(8.dp))
 Text(if (uiState.isScanning) "Scanning..." else "Scan for Devices")
 }

 if (uiState.isScanning) {
 LinearProgressIndicator(modifier = Modifier.fillMaxWidth())
 }

 if (uiState.discoveredDevices.isNotEmpty()) {
 Text(
 text = "Available Devices",
 style = MaterialTheme.typography.titleMedium
 )

 LazyColumn(
 modifier = Modifier
 .fillMaxWidth()
 .weight(1f)
 ) {
 items(uiState.discoveredDevices) { device ->
 DeviceListItem(
 device = device,
 onClick = { viewModel.connectToDevice(device.device) }
 )
 }
 }
 } else if (!uiState.isScanning) {
 Text(
 text = "No devices found. Try scanning again.",
 style = MaterialTheme.typography.bodyMedium,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 }
 }
 }
}

@Composable
fun DeviceConnectedCard(
 deviceName: String,
 batteryLevel: Int,
 onDisconnect: () -> Unit
) {
 Card(
 modifier = Modifier.fillMaxWidth(),
 ) {
 Column(
 modifier = Modifier.padding(16.dp),
 verticalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically
 ) {
 Icon(
 imageVector = Icons.Default.BluetoothConnected,
 contentDescription = "Connected device"
 )
 Spacer(modifier = Modifier.width(8.dp))
 Text(
 text = deviceName,
 style = MaterialTheme.typography.titleMedium
 )
 }

 Row(
 verticalAlignment = Alignment.CenterVertically
 ) {
 Icon(
 imageVector = Icons.Default.BatteryFull,
 contentDescription = "Battery level"
 )
 Spacer(modifier = Modifier.width(8.dp))
 Text(text = "Battery Level: $batteryLevel%")

 // Battery indicator
 LinearProgressIndicator(
 progress = { batteryLevel / 100f },
 modifier = Modifier
 .padding(start = 8.dp)
 .height(8.dp)
 .weight(1f)
 )
 }

 Button(
 onClick = onDisconnect,
 modifier = Modifier.align(Alignment.End)
 ) {
 Text("Disconnect")
 }
 }
 }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun DeviceListItem(
 device: BleDevice,
 onClick: () -> Unit
) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 4.dp),
 onClick = onClick
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Column(
 modifier = Modifier.weight(1f)
 ) {
 Text(
 text = device.name,
 style = MaterialTheme.typography.bodyLarge,
 maxLines = 1,
 overflow = TextOverflow.Ellipsis
 )
 Text(
 text = device.address,
 style = MaterialTheme.typography.bodySmall,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 }

 Text(
 text = "${device.rssi} dBm",
 style = MaterialTheme.typography.bodyMedium
 )
 }
 }
}
