 override fun onCharacteristicReadRequest(device: BluetoothDevice, requestId: Int, 
 offset: Int, characteristic: BluetoothGattCharacteristic) {
 Log.d(TAG, "onCharacteristicReadRequest: ${characteristic.uuid}")
