 // Respond with battery level
 val batteryLevel = batteryLevelState.value ?: 50 // Default value if not set
 bluetoothGattServer?.sendResponse(device, requestId, BluetoothGatt.GATT_SUCCESS, 0, byteArrayOf(batteryLevel.toByte()))
 }
 }

