 fun onError(message: String)
 }

 private var batteryCallback: BatteryCallback? = null

 fun setBatteryCallback(callback: BatteryCallback) {
 batteryCallback = callback
 }

 private val gattCallback = object : BluetoothGattCallback() {
 override fun onServicesDiscovered(gatt: BluetoothGatt, status: Int) {
 if (status == BluetoothGatt.GATT_SUCCESS) {
 val batteryService = gatt.getService(UUID.fromString("0000180F-0000-1000-8000-00805F9B34FB"))
 if (batteryService == null) {
 batteryCallback?.onError("Battery service not found")
 return
 }

 val batteryChar = batteryService.getCharacteristic(
 UUID.fromString("00002A19-0000-1000-8000-00805F9B34FB")
 )
 if (batteryChar == null) {
 batteryCallback?.onError("Battery characteristic not found")
 return
 }

 // Enable notifications
 val descriptor = batteryChar.getDescriptor(
 UUID.fromString("00002902-0000-1000-8000-00805F9B34FB") // Client Characteristic Configuration
 )
 descriptor?.let {
 it.value = BluetoothGattDescriptor.ENABLE_NOTIFICATION_VALUE
 gatt.writeDescriptor(it)
 }

 // Enable local notifications
 if (!gatt.setCharacteristicNotification(batteryChar, true)) {
 batteryCallback?.onError("Failed to enable notifications")
 return
 }

 // Read initial value
 if (!gatt.readCharacteristic(batteryChar)) {
 batteryCallback?.onError("Failed to read battery level")
 }
 } else {
 batteryCallback?.onError("Service discovery failed: $status")
 }
 }

 override fun onCharacteristicRead(
 gatt: BluetoothGatt,
 characteristic: BluetoothGattCharacteristic,
 status: Int
 ) {
 if (status == BluetoothGatt.GATT_SUCCESS) {
 when (characteristic.uuid) {
 UUID.fromString("00002A19-0000-1000-8000-00805F9B34FB") -> {
 val batteryLevel = characteristic.value[0].toInt() and 0xFF
 batteryCallback?.onBatteryLevelChanged(batteryLevel)
 }
 }
 } else {
 batteryCallback?.onError("Read characteristic failed: $status")
 }
 }

 // Add notification callback
 override fun onCharacteristicChanged(
 gatt: BluetoothGatt,
 characteristic: BluetoothGattCharacteristic
 ) {
 when (characteristic.uuid) {
 UUID.fromString("00002A19-0000-1000-8000-00805F9B34FB") -> {
 val batteryLevel = characteristic.value[0].toInt() and 0xFF
 batteryCallback?.onBatteryLevelChanged(batteryLevel)
 }
 }
 }
 }
}
```

Key improvements made:

1. Added a callback interface to communicate battery updates to the UI
2. Added proper error handling and reporting
3. Added the missing descriptor write for enabling notifications
4. Added the `onCharacteristicChanged` callback to receive continuous updates
5. Added null checks and error reporting for service and characteristic discovery
6. Added status checking for GATT operations

To use this in your Activity/Fragment:

```kotlin:app/src/main/java/com/peri/YourActivity.kt
